package project.spring.apirestpet.implementation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import project.spring.apirestpet.entity.Pet;import project.spring.apirestpet.repository.PetRepository;import project.spring.apirestpet.service.PetService;import java.util.List;@Servicepublic class PetServiceImpl implements PetService {    @Autowired    private PetRepository petRepository;    @Override    public List<Pet> findAll() {        return petRepository.findAll();    }    @Override    public Pet findById(Long id) {        return petRepository.findById(id).orElse(null);    }    @Override    public Pet save(Pet pet) {        return petRepository.save(pet);    }    @Override    public Pet update(Long id, Pet pet) {        Pet data = petRepository.findById(id).orElse(pet);        data.setName(pet.getName());        data.setAge(pet.getAge());        data.setSpecies(pet.getSpecies());        data.setBreed(pet.getBreed());        return petRepository.save(data);    }    @Override    public void delete(Long id) {        petRepository.deleteById(id);    }}