package project.spring.apirestpet.implementation;import org.modelmapper.ModelMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import project.spring.apirestpet.model.dto.PetDto;import project.spring.apirestpet.model.entity.Pet;import project.spring.apirestpet.repository.PetRepository;import project.spring.apirestpet.service.PetService;import java.util.List;import java.util.stream.Collectors;@Servicepublic class PetServiceImpl implements PetService {    @Autowired    private PetRepository petRepository;    @Autowired    private ModelMapper modelMapper;    private PetDto convertToDto(Pet pet) {        return modelMapper.map(pet, PetDto.class);    }    private Pet convertToEntity(PetDto petDto) {        return modelMapper.map(petDto, Pet.class);    }    @Override    public List<PetDto> findAll() {        List<Pet> pets = petRepository.findAll();        return pets.stream()                .map(this::convertToDto)                .collect(Collectors.toList());    }    @Override    public PetDto findById(Long id) {        return convertToDto(petRepository.findById(id).orElse(null));    }    @Override    public PetDto save(PetDto petDto) {        Pet data = convertToEntity(petDto);        Pet insert = petRepository.save(data);        return convertToDto(insert);    }    @Override    public PetDto update(Long id, PetDto petDto) {        Pet pet = petRepository.findById(id).orElse(convertToEntity(petDto));        pet.setName(petDto.getName());        pet.setAge(petDto.getAge());        pet.setSpecies(petDto.getSpecies());        pet.setBreed(petDto.getBreed());        Pet edit = petRepository.save(pet);        return convertToDto(edit);    }    @Override    public void delete(Long id) {        petRepository.deleteById(id);    }}