package project.spring.apirestpet.repository;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;import project.spring.apirestpet.entity.Pet;import java.util.List;import java.util.Optional;import static org.junit.jupiter.api.Assertions.*;@DataJpaTestclass PetRepositoryTest {    @Autowired    private PetRepository petRepository;    @Test    void select_all_pet_when_successful(){        List<Pet> result=petRepository.findAll();        assertNotNull(result);        assertEquals(2,result.size());    }    @Test    void find_by_id_pet_when_successful(){        long id=1L;        Optional<Pet> pet=petRepository.findById(id);        assertEquals(id,pet.get().getId());    }    @Test    void save_pet_when_successful() {        Pet pet = Pet.builder().name("lu").age(11).species("cat").breed("persa").build();        Pet savePet = petRepository.save(pet);        assertNotNull(savePet.getId());        assertEquals(savePet.getName(), pet.getName());    }    @Test    void update_pet_when_successful() {        Pet pet = Pet.builder().name("lu").age(11).species("cat").breed("persa").build();        Pet savePet = petRepository.save(pet);        savePet.setName("mini");        Pet updatePet = petRepository.save(savePet);        assertNotNull(savePet.getId());        assertEquals(savePet.getName(), updatePet.getName());    }    @Test    void delete_pet_when_successful() {        Pet pet = Pet.builder().name("lu").age(11).species("cat").breed("persa").build();        Pet savePet = petRepository.save(pet);        petRepository.deleteById(savePet.getId());        Optional<Pet> result = petRepository.findById(savePet.getId());        assertTrue(result.isEmpty());    }}